package hello;

public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int num=94;
       if(num%2==0)
       {
    	   System.out.print(num +" is Even");
       }
       else
       {
    	   System.out.print(num+"is Odd ");
       }
	}

}
